mapper.select:
SELECT DISTINCT AT .ID, AT .TITLE, TC. NAME CATE_NAME, AT .CREATE_TIME, AT.SUMMARY, AT .DRAFT, AT .APPROVE_CNT, AT .VIEWS, AT .COMMENTED, AT .APPRECIABLE, AT .TOP FROM T_ARTICLE AT LEFT JOIN T_CATE TC ON TC.ID = AT .CATE_ID LEFT JOIN T_TAG_REFER TTR ON TTR.REFER_ID = `AT`.ID LEFT JOIN T_TAG TT ON TT.ID = TTR.TAG_ID
AT.TITLE LIKE CONCAT('%',#{title},'%')
OR AT.TEXT_CONTENT LIKE CONCAT('%',#{textContent},'%')
AND TC.ID = #{cateId}
AND TT.NAME = #{tag}
ORDER BY AT.TOP DESC,AT.CREATE_TIME DESC

mapper.update:
    UPDATE T_ARTICLE
		SET VIEWS = VIEWS + 1
    WHERE ID = #{id,jdbcType=BIGINT}
